mapper.select:
SELECT * FROM user
WHERE id != #{currentUserId}
  AND (username LIKE #{keyword}
   OR nickname LIKE #{keyword}
   OR CAST(id AS CHAR) = REPLACE(#{keyword}, '%', ''))
    LIMIT 20

select id, username, nickname, password, user_profile as avatar, email, create_time, update_time, status
from user

    and id = #{id}
    and username = #{username}
    and nickname = #{nickname}
    and password = #{password}
    and user_profile = #{avatar}
    and email = #{email}
    and create_time = #{createTime}
    and update_time = #{updateTime}
    and status = #{status}

mapper.update:
update user
username = #{username}, nickname = #{nickname}, password = #{password}, user_profile = #{avatar}, email = #{email}, create_time = #{createTime}, update_time = #{updateTime}, status = #{status}, user_state_id = #{userStateId}, enabled = #{enabled}, is_locked = #{locked},
where id = #{id}

mapper.select:
select * from user where 1=1

    and nickname like concat('%',#{keyword},'%')


    and is_locked = #{isLocked}


    and status = #{status}


    limit #{page},#{size}

select count(*) from user where 1=1

    and nickname like concat('%',#{keyword},'%')


    and is_locked = #{isLocked}


    and status = #{status}
